INSERT INTO `tematike` (`idt`, `ime_tematike`, `opis`, `posnetek`, `digitalno_potrdilo`, `tel`, `video_link`, `info-link`, `Page-link`, `icon`) VALUES
(1, 'eNapotnica', 'E-napotnica je elektronska oblika fizične napotnice, ki se jo prejme pri osebnem zdravniku. Napotnice ni potrebno prevzeti v ambulanti zdravnika, ampak je možno podatke o njej pridobiti tudi preko telefona, elektornske pošte ali z vpogledom v osebni profil na portalu eZdravje.', 0, 0, '080 24 25', '', 'http://www.mz.gov.si/si/pogoste_vsebine_za_javnost/projekt_e_zdravje/enapotnica/', 'https://narocanje.ezdrav.si/', ''),
(2, 'eRecept', 'E-recept je elektronsko predpisovanje zdravil. Oseba, ki se registrira na portal eZdravje, lahko za določeno časovno obdobje pregleduje na svoje ime izdane recepte.', 1, 1, '080 2445', '', 'http://www.nijz.si/sl/erecept-0', 'https://zvem.ezdrav.si/', ''),
(3, 'Pridobivanje študentskih bonov', 'Študentski boni so subvencionirani boni za prehrano študentov. Prva prijava za pridobitev študentskih bonov se opravi preko spletnega obrazca. Nato se študent s pridobljeno kodo ob koncu prijavnega postopka, s potrdilom o vpisu in z osebnim telefonom osebno zglasi na eni izmed info točk subvencionirane študentske prehrane v Sloveniji. ', 1, 0, '031 452 000', '', 'https://soup.si/sl/prehrana/vse-o-prehrani', 'https://www.studentska-prehrana.si/sl/Account/Register', ''),
(4, 'Prijava začasnega prebivališča', 'Oseba ima lahko poleg stalnega tudi začasno prebivališče. Vlogo za prijavo slednjega se vloži pri upravni enoti. Začasno prebivališče je potrebno prijaviti v primeru, ko bo državljan ali tujec z dovoljenjem za stalno prebivanje, ki začasno prebiva zunaj naslova stalnega prebivališča, vendar ne pri stanodajalcu ali gostitelju, na tem naslovu prebival več kot 90 dni.', 1, 1, '080 2002', '', 'http://www.mnz.gov.si/si/mnz_za_vas/osebni_dokumenti_in_prebivalisce/prijava_in_odjava_prebivalisca/zacasno_prebivalisce/', 'https://e-uprava.gov.si/podrocja/vloge/vloga.html?id=1164', ''),
(5, 'Prijava v evidenco brezposelnih', 'Evidenca brezposelnih je baza Zavoda Reublike Slovenije za zaposlovanje o brezposelnih osebah na področju Republike Slovenije. V evidenco so uvrščene tiste osebe, ki niso v delovnem razmerju, niso samozaposlene ali upokojene, nimajo statusa kmeta in se ne izobražujejo, so pa sposobne za delo in želijo aktivno iskati zaposlitev. Postopek vključitve v evidenco je možno urediti elektronsko na portalu poiscidelo.si. Po registraciji in prijavi v portal, se v zgornjem desnem kotu izbere gumb \"Moji dokumenti\", nato pa med ponujenimi možnostmi še \"Evidenca iskalcev brezposelnih oseb.', 1, 1, '080 20 55', '', 'https://www.ess.gov.si/iskalci_zaposlitve/prijava_brezposelne_osebe', 'https://www.poiscidelo.si', ''),
(6, 'Prijava v evidenco iskalcev zaposlitve', 'Evidenca iskalcev zaposlitve je baza Zavoda Republike Slovenije za zaposlovanje o brezposelnih osebah na področju Slovenije, ki aktivno iščejo zaposlitev. Postopek prijave v evidenco je možno opraviti elektronsko na portalu poiscidelo.si. Po registraciji in prijavi v portal se v zgornjem desnem kotu izbere gumb \"Moji dokumenti\", nato pa med ponujenimi možnostmi še \"Evidenca iskalcev zaposlitve\".', 1, 1, '080 20 55', '', 'https://www.ess.gov.si/iskalci_zaposlitve/prijava_brezposelne_osebe/evidenca-iskalcev-zaposlitve', 'https://www.poiscidelo.si/', ''),
(7, 'Vloga za denarno nadomestilo', 'Denarno nadomestilo je nadomestilo za brezposelnost. Namenjeno je brezposelnim osebam, ki živijo na območju Republike Slovenije. Za pridobitev se je potrebno najprej prijaviti na Zavod RS za zaposlovanje in nato v tridesetih (30.) dneh po prenehanju obveznega zavarovanja vložiti vlogo za prejemanje denarnega nadomestila. Postopek oddaje vloge je možno opraviti elektronsko na portalu poiscidelo.si. Po registraciji in prijavi v omenjen portal najprej v zgornjem desnem kotu izberite gumb \"Moji dokumenti\", nato pa med ponujenimi možnostmi še \"Vloga za denarno nadomestilo\".', 1, 1, '080 2055', '', 'https://www.ess.gov.si/iskalci_zaposlitve/prijava_brezposelne_osebe/denarno_nadomestilo', 'https://www.poiscidelo.si/', ''),
(8, 'Portal eSvetovanje', 'Portal eSvetovanje je spletni portal za učinkovito iskanje zaposlitve in načrtovanje kariere. Za dostop do vseh storitev je priporočljiva začetna registracija. Prijavljen uporabnik lahko na portalu v osebnem profilu shranjuje podatke in dostopa do shranjenih rezultatov izpolnjenih vprašalnikov. Za učinkovito izkoriščeno pot do zaposlitve, portal uporabnika usmerja proti odgovorom na osebnostna in karierna vprašanja, kot so: Kakšen sem? Kaj znam? Kaj me zanima? Kaj naj delam? Kje iščem zaposlitev? Kako kandidiram na delovno mesto? Kako naj bom aktiven?', 1, 1, '080 2055', '', '', 'https://esvetovanje.ess.gov.si/', ''),
(9, 'Vloga za brezplačno pravno pomoč', 'Brezplačna pravna pomoč je pravna pomoč, namenjena osebam, ki si slednje zaradi finančnih razlogov ne morejo privoščiti. Upravičenci lahko zaprosijo za celotno ali zgolj delno pokritje stroškov pravne pomoči in sodnega postopka. Vložitev prošnje za pomoč je možna osebno ali po pošti. O ugoditvi prošnje odloča pristojni organ tistega sodišča, na območju katerega ima prosilec stalno ali začasno prebivališče.', 1, 1, '080 2002', '', 'https://e-uprava.gov.si/podrocja/drzava-druzba/pravna-pomoc/brezplacna-pravna-pomoc.html', 'https://e-uprava.gov.si/podrocja/drzava-druzba/pravna-pomoc/brezplacna-pravna-pomoc.html', ''),
(10, 'Napoved za odmero dohodnine na letni ravni', 'Dohodnina je davek od dohodka fizičnih oseb. Odmera dohodnine se izvede enkrat letno. ', 1, 1, '01 369 3000', '', 'http://www.fu.gov.si/davki_in_druge_dajatve/podrocja/dohodnina/letna_odmera_dohodnine/', 'https://edavki.durs.si/EdavkiPortal/[953031]/PersonalPortal/CommonPages/Documents/New.aspx', ''),
(11, 'Vloga za ugovor zoper informativni izračun dohodnine', 'V primeru, da ugotovite, da so podatki v informativnem izračunu dohodnine nepravilni ali nepopolni, lahko v petnajstih (15) dneh od vročitve informativnega izračuna vložite ugovor. Za dan vročitve se upošteva 15. dan od datuma, ki je na izračunu označen kot datum odpreme izračuna iz Finančnega urada na vaš naslov. Od tega dne imate nato za ugovor časa še 15 dni.', 1, 1, '01 369 3000', '', 'https://www.prva.si/kako-se-pritozimo-na-informativni-izracun-dohodnine/', 'http://www.fu.gov.si/fileadmin/Internet/Davki_in_druge_dajatve/Podrocja/Dohodnina/Letna_odmera_dohodnine/Obrazci/Ugovor_2017.pdf', ''),
(12, 'Zahtevek za namenitev dela dohodnine za donacije', 'Vsak zavezanec za dohodnino lahko del svoje odmerjene dohodnine nameni za donacijo organizaciji, ki deluje s splošno koristnim namenom.', 1, 0, '01 369 3000', '', 'https://www.informiran.si/portal.aspx?content=dohodnina-donacije&showMenu=1&showRightFrame=1', 'https://bit.ly/2Calrd2', ''),
(13, 'Vloga za odpis, odlog in obročno plačilo davka v največ treh mesečnih obrokih za fizične osebe', 'Davčni zavezanec ima pravico, da odmerjen davek poravna v več obrokih. Poleg odpisa je možen tudi odlog ali obročno plačilo na 3, 12 ali največ 24 obrokov. ', 1, 0, '01 369 3000', '', 'http://www.saop.si/poslovne-informacije/novice/davki-in-prispevki/odpis-odlog-obrocno-placilo-davka-4354/', 'https://bit.ly/2slzGWj', ''),
(14, 'Razkritje podatkov o obveznih prispevkih za socialno varnost iz delovnega razmerja', 'Opis postopka za pridobitev pravice do razkritja podatkov o obveznih prispevkih za socialno varnost iz delovnega razmerja.', 1, 0, '01 369 3000', '', 'https://data.si/blog/2017/08/28/prispevki-za-socialno-varnost/', 'https://bit.ly/2LEOpU8', ''),
(15, 'Mednarodna obdavčitev rezidenta RS', 'Rezidenti Slovenije so dolžni plačati dohodnino od vseh dohodkov, ki imajo vir v Sloveniji ali izven nje. ', 1, 0, '01 369 3000', '', 'http://www.fu.gov.si/davki_in_druge_dajatve/podrocja/mednarodno_obdavcenje/', 'https://bit.ly/2IUAag0 ', ''),
(16, 'Pravice iz delovnega razmerja', 'Delovno razmerje je razmerje med zaposlenim in delodajalcem, v katerem sta obe strani dolžni izvrševati dogovorjene in predpisane obveznosti in pravice.', 1, 0, '01 369 3000', '', 'http://www.mddsz.gov.si/si/delovna_podrocja/delovna_razmerja_in_pravice_iz_dela/vodnik_po_pravicah_iz_delovnega_razmerja/', 'https://bit.ly/2jlNgE5', ''),
(17, 'Vloga za uveljavljanje pravice do dodatka za pomoč in postrežbo z vprašalnikom', 'Dodatek za pomoč in postrežbo pridobi oseba, ki fizično ni zmožna samostojno opravljati vseh ali večine življenjskih potreb.\r\nČe zaradi trajnih sprememb v zdravstvenem stanju oseba ne morete zadovoljevati vseh ali večine življenjskih potreb, lahko uveljavlja nujno potrebo po stalni pomoči in postrežbi.', 1, 0, '080 2002', '', '', 'https://e-uprava.gov.si/si/podrocja/sociala-zdravje-smrt/socialne-pomoci/dodatek-za-pomoc-in-postrezbo', ''),
(18, 'Zahteva za priznanje pravice do dodatka za pomoč in postrežbo', 'Vojni veterani lahko s to vlogo uveljavite priznanje dodatka za pomoč in postrežbo, ko ga ne morete uveljaviti po drugih predpisih. ', 1, 1, '080 2002', '', '', 'http://e-uprava.gov.si/podrocja/vloge/vloga.html?id=463', ''),
(19, 'Upokojitev ', 'Upokojitev je prenehanje statusa delovno aktivnega prebivalca. Tu prikazujemo obstoječe vrste pokojnin v Republiki Sloveniji, možno koriščenje delne in predčasne upokojitve, ugodnosti ob poznejši upokojitvi ter možnost prijave v dom za starejše.', 1, 0, '080 2002', '', 'https://e-uprava.gov.si/podrocja/delo-upokojitev/upokojitev.html', '', ''),
(20, 'Pregled porabljenih študentski subvencij', 'Študentom je omogočeno spremljanje porabe študentskih bonov. To jim omogoča vpogled v lokale, kjer so koristili bone, vpogled v cene doplačil ter mesečni seštevek vseh doplačil.', 1, 0, '031 452 000', '', 'https://www.sou-lj.si/sl/vsebina/faq', 'https://www.studentska-prehrana.si/sl/transactions', ''),
(21, 'Subvencije potniškega prometa', 'Študentje imajo možnost pridobitve subvencije za javni potniški prevoz in sicer glede na kraj prebivališča in kraj izobraževanja. ', 1, 0, '0', '', 'https://subvencije.ijpp.si/', 'https://subvencije.ijpp.si/', ''),
(22, 'eNaročanje', 'E-naročanje je elektronsko naročanje k zdravniku specialistu. Danes je naročanje najhitrejše preko portala eZdravje, še vedno pa lahko osebo na pregled k specialistu naroči tudi njen osebni zdravnik.', 1, 0, '080 2425', '', 'https://sobotainfo.com/novica/globalno/kako-poteka-enarocanje/130826', 'https://narocanje.ezdrav.si/', ''),
(23, 'Štipendija', 'Štipendije so namenjene finančni pomoči študentom in vzpodbujanju njihovega truda v času študija. Tu najdete nabor različnih štipendij, njihovo višino, trajanje prejemanja, način izplačevanja ter zahteve za pridobitev.', 1, 0, '01 369 77 00', '', 'http://www.mddsz.gov.si/si/delovna_podrocja/trg_dela_in_zaposlovanje/stipendije/', 'http://www.mddsz.gov.si/fileadmin/mddsz.gov.si/pageuploads/Obr_drzavnastipendija.pdf', ''),
(24, 'eČakalne dobe\r\n', 'E-čakalne dobe je elektronski pregled čakalnih dob na portalu eZdravje. Te je možno preveriti za posamezno zdravstveno storitev in/ali posamezno zdravstveno ustanovo v Republiki Sloveniji.', 1, 0, '080 2445\r\n', 'https://www.youtube.com/watch?v=n-Nbxz8fEd0&feature=youtu.be', 'http://www.nijz.si/sl/podrocja-dela/cakalne-dobe', 'https://cakalnedobe.ezdrav.si/', ''),
(27, 'Subvencija za bivanje študentov', 'Študentje imajo pravico do uveljavljanja subvencije za bivanje v kraju izobraževanja. Subvencijo je možno uveljavljati za bivanje v javnih in zasebnih študentskih domovih in za bivanje pri zasebnikih. ', 0, 1, '080 2002', '', 'http://www.studentska-org.si/studentski-kazipot/bivanje/studentski-domovi/', 'https://e-uprava.gov.si/podrocja/izobrazevanje-kultura/visoka-in-visja-sola/subvencija-za-bivanje-studentov.html', ''),
(28, 'Vloga za digitalno potrdilo', 'Digitalno potrdilo je elektronska osebna izkaznica posameznika. Vsebuje osebne podatke o imetniku, podatke o izdajatelju potrdila ter obdobje njegove veljavnosti. Digitalno potrdilo olajša birokracijo, ki jo ima posamezni državljan z državo. Prihrani čas in stroške. Digitalno potrdilo lahko vsak državljan pridobi na katerikoli upravni enoti znotraj območja Repblike Slovenije. ', 0, 0, '080 2002\r\n', '', 'http://evem.gov.si/info/zacenjam/digitalna-potrdila/', 'https://www.sigen-ca.si/prevzem-spletno.php', ''),
(29, 'Prevzem digitalnega potrdila', 'Prevzem digitalnega potrdila pomeni namestitev digitalnega potrdila na vaš računalnik. To lahko nato uporabljate na vseh računalnikih, na katere ste potrdilo dodali. Nekateri brskalniki prevzema digitalnega potrdila ne omogočajo. Potrdila torej ni mogoče prevzeti preko brskalnikov Opera, Google Chrome in Microsoft Edge.', 0, 0, '080 20 55', '', 'https://edavki.durs.si/OpenPortal/Pages/Registration/DigicertRequestIndividual.aspx', 'https://www.sigen-ca.si/prevzem-spletno.php', ''),
(30, 'Vloga za smsPASS (če že imate digitalno potrdilo)', 'SmsPASS je mobilna identiteta. Uporablja se za mobilno izvajanje birokracije med državljanom in državo. Omogoča elektronsko podpisovanje dokumentov in zanesljivo identifikacijo uporabnika pri uporabi elektronskih storitev slovenske javne uprave. V primeru, da je oseba imetnica diitalnega potrdila, je postopek pridobitve smsPASS-a prikazan tu.', 0, 1, '080 2002', '', 'http://www.si-ca.si/dokumenti/Aktivacija-smsPASS.pdf', 'https://e-uprava.gov.si/pomoc-kontakt/pomoc-pri-uporabi/sms-pass.html', ''),
(31, 'Vloga za smsPASS (če nimate digitalnega potrdila)', 'SmsPASS je mobilna identiteta. Uporablja se za mobilno izvajanje birokracije med državljanom in državo. Omogoča elektronsko podpisovanje dokumentov in zanesljivo identifikacijo uporabnika pri uporabi elektronskih storitev slovenske javne uprave. V primeru, da oseba ni imetnica digitalnega potrdila, je postopek pridobitve smsPASS-a prikazan tu.', 0, 0, '080 2002', '', 'http://www.si-ca.si/dokumenti/Aktivacija-smsPASS.pdf', 'https://e-uprava.gov.si/pomoc-kontakt/pomoc-pri-uporabi/sms-pass.html', ''),
(32, 'Ustvarjanje uporabniškega računa SI-PASS', 'SI-PASS je sistem za preverjanje elektronske identitete posameznikov. Prijava s SI-PASS uporabniškim računom je pogoj za kasnejšo prijavo z digitalnim in/ali mobilnih potrdilom. Uporabniški račun se ustvari z uporabo elektronskega naslova, gesla ter s končno potrditvijo prijave preko dobljenega elektronskega sporočila. ', 1, 0, '080 2002', 'https://www.youtube.com/watch?v=rOoO4KGJBPE', 'http://www.si-ca.si/dokumenti/Registracija-SI-PASS.pdf', 'https://sicas.gov.si/bl/login?entityID=SICAS_USER-PAGES', ''),
(33, 'Nastavitve uporabniškega računa SI-PASS', 'Osebam s SI-PASS uporabniškim računom so no voljo tudi nastavitve. V sklopu le teh, lahko uporabniki urejajo in ali spreminajo svoja gesla in naslove e-pošte.', 0, 1, '080 2002', '', '', 'https://sicas.gov.si/IdP-RM-Front/protected/account.htm', ''),
(35, 'Pravice upokojenskega statusa', 'Osebe s statusom upokojenca imajo pravico do uveljavljanja določenih prednosti. Te pravice so določene s pravili Zavoda za pokojninsko in invalidsko zavarovanje Republike Slovenije (ZPIZ) in zato podrobneje opisane na njihovi spletni strani. ZPIZ ima status javnega zavoda, s čimer si pridržuje pravico do izvajanja enotnega obveznega pokojninskega in invalidskega zavarovanja na območju Republike Slovenije. ', 0, 1, '01 474 51 00', '', 'https://www.zpiz.si/cms/?ids=content&inf=25', 'https://www.zpiz.si/cms/content/obrazcivlog', ''),
(36, 'Vloga za uveljavljanje posebne dohodninske olajšave za vzdrževane družinske člane', 'Posebna dohodninska olajšava za vzdrževane družinske člane je namenjena nižanju davčne osnove zavezancev, ki imajo v svojem gospodinjstvu vzdrževane družinske člane.', 1, 0, '01 369 3000', '', 'https://data.si/blog/2017/12/29/olajsava-za-vzdrzevane-druzinske-clane/', 'https://bit.ly/2kuEHXZ ', ''),
(37, 'Vloga za poročilo o doseženem dohodku iz naslova osebnega dopolnilnega dela', 'V primeru, da se ukvarjate z osebnim dopolnilnim delom, morate Finančni upravi oddati poročilo o doseženem dohodku znotraj tega dela.', 0, 1, '01 369 3000', '', 'https://data.si/blog/2015/03/20/katere-obrazce-morajo-oddajati-izvajalci-dopolnilnega-dela/', 'https://bit.ly/2JcFHOn', ''),
(38, 'Znižano plačilo vrtca', 'Znižano plačilo vrtca lahko uveljavljajo starši, katerih otroci obiskujejo javni vrtec, zasebni vrtec s koncesijo ali zasebni vrtec, financiran iz občinskega proračuna. Pravica znižanega plačila vrtca se pridobi s prvim dnem naslednjega meseca po oddani vlogi. V kolikor je oseba vlagateljica imetnica digitalnega potrdila, je vlogo za znižano plačilo vrtca moč oddati tudi elektronsko na portalu eUprava.', 0, 1, '080 2002', '', 'http://www.mddsz.gov.si/si/uveljavljanje_pravic/socialna_zakonodaja/znizano_placilo_vrtca/', 'http://e-uprava.gov.si/podrocja/vloge/vloga.html?id=1599&', ''),
(39, 'Prijava otroka v vrtec', 'Starši lahko otroka v vrtec vpišejo pri otrokovih dopolnjenih enajstih (11) mesecih. Starši lahko otroka vpišejo v katerikoli vrtec znotraj Republike Slovenije, neglede na družinsko stalno prebivališče. V kolikor imajo vrtci prosta mesta za sprejem novih otrok, se lahko vpis opravi kadarkoli tekom koledarskega leta. Po uspešno opravljenem elektronskem vpisu, so starši s strani vrtca obveščeni o pogojih in datumu vključitve.', 0, 0, '080 2002', '', 'https://e-uprava.gov.si/podrocja/druzina-otroci-zakonska-zveza/vrtci-varovanje-otrok/vpis-otroka-v-vrtec.html', 'https://e-uprava.gov.si/podrocja/vloge/vloga.html?id=2129', ''),
(40, 'Socialna pomoč za otroke', 'Otroški dodatek je dopolnilni prejemek staršev za preživljanje, vzgojo in izobraževanje otroka. Pravico za prejemanje slednjega se uveljavlja pri centru za socialno delo in sicer v kraju, kjer ima oseba stalno prebivališče.\r\n', 0, 1, '080 2002', '', 'http://www.mddsz.gov.si/si/delovna_podrocja/druzina/starsevsko_varstvo_in_druzinski_prejemki/druzinski_prejemki/otroski_dodatek/', 'https://e-uprava.gov.si/podrocja/druzina-otroci-zakonska-zveza/pravice-prejemki-druzine/otroski-dodatek.html', ''),
(42, 'Naročanje potrdil iz evidenc zavoda', 'Pri Zavodu za zaposlovanje Republike Slovenije je možno naročiti potrdila iz različnih evidenc. Potrdila, ki jih je možno naročiti, so Potrdilo o prijavi v evidenci brezposelnih oseb, Potrdilo o prejemanju denarnega nadomestila in Potrdilo o prejemanju Zoisove štipendije, pridobljene na ZRSZ pred 1.9.2008. Vsa potrdila Zavod RS za zaposlovanje na dom pošlje v roku treh delovnih dni..', 1, 0, '080 20 55', '', '', 'https://www.poiscidelo.si/', ''),
(43, 'Kritje prispevka za obvezno ZZ ', 'Kritje prispevka za obvezno zdravstveno zavarovanje je namenjeno tistim državljanom Republike Slovenije, ki so upravičeni do denarne socialne pomoči. Ko je osebi pravica do plačila prispevka za obvezno zdravstveno zavarovanje priznana, ji Zavod za zdravstveno zavarovanje Slovenije uredi obvezno zdravstveno zavarovanje.', 1, 0, '0', '', '', 'https://e-uprava.gov.si/podrocja/sociala-zdravje-smrt/zdravje/zdravstveno-zavarovanje-socialno-ogrozenih-oseb.html', '');
